// Type decls to allow for asset imports

declare module '*.fbx' {
    const content: any;
    export default content;
}

declare module '*.jpg' {
    const content: any;
    export default content;
}

declare module '*.png' {
    const content: any;
    export default content;
}

declare module '*.hdr' {
    const content: any;
    export default content;
}
